mapper.select:
SELECT

FROM

WHERE 1=1

    AND USER_ID

        ${operators.userId}

        =

    #{userId}


    AND USER_CODE

        ${operators.userCode}

        =

    #{userCode}


    AND USER_NAME

        ${operators.userName}

        =

    #{userName}


    AND USER_NAME_EN

        ${operators.userNameEn}

        =

    #{userNameEn}


    AND USER_NAME_PY

        ${operators.userNamePy}

        =

    #{userNamePy}


    AND USER_NAME_FR

        ${operators.userNameFr}

        =

    #{userNameFr}


    AND ORG_CODE = #{orgCode}


    AND POST_NO = #{postNo}


    AND USER_SEX = #{userSex}


    AND NATIONALITY = #{nationality}


    AND ETHNIC = #{ethnic}


    AND NATIVE_PLACE = #{nativePlace}


    AND BIRTH_PLACE = #{birthPlace}


    AND BIRTH_DATE = #{birthDate}


    AND ID_DECIMAL = #{idDecimal}


    AND PHOTO_ID = #{photoId}


    AND JOIN_DATE = #{joinDate}


    AND STATUS = #{status}


    AND TELLER_ID = #{tellerId}


    AND RESIDENTIAL_ADDRESS = #{residentialAddress}


    AND MOBILE_PHONE = #{mobilePhone}


    AND REMARK = #{remark}


    AND PASSWORD = #{password}


    AND SALT = #{salt}


    AND ONLINE_STATUS = #{onlineStatus}


    AND IP_ADDR = #{ipAddr}


    AND USER_THEME = #{userTheme}


    AND PD_COUNT = #{pdCount}


    AND PD_MODTIME = #{pdModtime}


    AND PD_LOCKTIME = #{pdLocktime}


    AND LAST_SIGNON_TIME = #{lastSignonTime}


    AND LAST_SIGNOUT_TIME = #{lastSignoutTime}


    AND LEGAL_ORG = #{legalOrg}


    AND CRT_USER_CODE = #{crtUserCode}


    AND CRT_ORG_CODE = #{crtOrgCode}


    AND CRT_DATE = #{crtDate}


    AND UPD_USER_CODE = #{updUserCode}


    AND UPD_ORG_CODE = #{updOrgCode}


    AND UPD_DATE = #{updDate}


    AND ORG_CODE ${_orgAuth}


    AND USER_CODE ${_userAuth}
